function inputFocus() {
    if ($(".mob-no").val() == "") {
        $(".mob-no").parent().removeClass("focus");
    } else {
        $(".mob-no").parent().addClass("focus");
    }
    $(".mob-no").focus(function () {
        $(this).parent().addClass("focus");
    });
    $(".mob-no").blur(function () {
        if ($(this).val() == "") {
            $(this).parent().removeClass("focus");
        }
    });

}



